function parseSingleGame(element) {
  const lines = element
    .split('\n')
    .map(line => line.trim())
    .filter(line => line);

  if (lines.length < 4) {
    return null;
  }

  try {
    const game = {};

    // Extract image URL et app ID
    const imageUrlMatch = lines[0].match(
      /\[https:\/\/shared\.(?:cloudflare|akamai)\.steamstatic\.com\/store_item_assets\/steam\/apps\/(\d+)\/([^\]]+)\]/
    );
    if (imageUrlMatch) {
      game.app_id = Number(imageUrlMatch[1]);
      game.image_url = lines[0].replace(/^\[|\]$/g, '');
    }

    // Title (ligne aprÃ¨s l'URL de l'image)
    game.title = lines[1] || "";

    // Date (ligne aprÃ¨s le titre)
    game.date = lines[2] || "";

    // Parse discount, prices, and store URL
    const prices = [];
    let discount = null;
    let store_url = null;

    for (const line of lines.slice(3)) {
      if (/^-?\d+%$/.test(line)) {
        // Discount percentage
        discount = line;
      } else if (/^\d+,\d+â‚¬$/.test(line)) {
        // Price (format XX,XXâ‚¬)
        prices.push(line);
      } else if (line.includes('store.steampowered.com')) {
        // Store URL
        store_url = line.replace(/^\[|\]$/g, '');
      }
    }

    game.discount = discount;
    game.original_price = prices.length > 1 ? prices[0] : null;
    game.current_price = prices.length ? prices[prices.length - 1] : null;
    game.store_url = store_url;

    // Calculer si c'est gratuit
    game.is_free = game.current_price === '0,00â‚¬';

    return game;
  } catch (e) {
    console.error('Erreur lors du parsing:', e);
    return null;
  }
}

