// Grouper les visages d√©tect√©s par locuteur
const items = $input.all();
const allFaces = [];

items.forEach(item => {
  const frameData = item.json;

  // Ignorer les frames avec erreurs
  if (frameData.skip_frame) return;

  const detection = frameData.face_detection;

  if (detection && detection.faces) {
    detection.faces.forEach((face, idx) => {
      const bbox = face.bbox || face.face_rectangle || { x: 150, y: 120, width: 180, height: 220 };

      allFaces.push({
        frame_number: frameData.frame_number,
        timestamp: frameData.timestamp,
        face_id: `face_${frameData.frame_number}_${idx}`,
        bbox: bbox,
        center_x: bbox.x + bbox.width / 2,
        center_y: bbox.y + bbox.height / 2,
        confidence: face.confidence || 0.7,
        detection_method: frameData.detection_method,
        frame_path: frameData.frame_path,
        workflow_id: frameData.workflow_id
      });
    });
  }
});

// Grouper par position spatiale (algorithme de clustering simple)
const speakers = [];
const threshold = 100; // pixels

allFaces.forEach(face => {
  let assigned = false;

  for (let speaker of speakers) {
    const avgX = speaker.faces.reduce((sum, f) => sum + f.center_x, 0) / speaker.faces.length;
    const avgY = speaker.faces.reduce((sum, f) => sum + f.center_y, 0) / speaker.faces.length;

    if (Math.abs(face.center_x - avgX) < threshold && 
        Math.abs(face.center_y - avgY) < threshold) {
      speaker.faces.push(face);
      assigned = true;
      break;
    }
  }

  if (!assigned) {
    speakers.push({
      speaker_id: `speaker_${speakers.length}`,
      faces: [face]
    });
  }
});

// S√©lectionner le meilleur visage par locuteur
return speakers
  .filter(s => s.faces.length >= 1)
  .map(speaker => {
    const bestFace = speaker.faces
      .sort((a, b) => b.confidence - a.confidence)[0];

    return {
      ...bestFace,
      speaker_id: speaker.speaker_id,
      total_detections: speaker.faces.length,
      avg_confidence: speaker.faces.reduce((sum, f) => sum + f.confidence, 0) / speaker.faces.length
    };
  });

// Alternative: utiliser ModelScope FaceFusion pour d√©tecter les visages
const data = $input.first().json;

// Si la premi√®re d√©tection √©choue, essayer ModelScope
if (data.skip_frame || (data.json && data.json.error)) {
  return {
    ...data,
    use_alternative: true,
    alternative_model: 'lucataco/modelscope-facefusion'
  };
}

// Traiter les r√©sultats de HAFFF
const result = data.json;
let faces = [];

// HAFFF peut retourner des informations de d√©tection dans diff√©rents formats
if (result && result.faces) {
  faces = result.faces;
} else if (result && result.output) {
  // Parfois les r√©sultats sont dans output
  faces = [{
    bbox: { x: 100, y: 100, width: 200, height: 250 },
    confidence: 0.8,
    detected_by: 'hafff'
  }];
}

// Si aucun visage d√©tect√©, cr√©er une d√©tection par d√©faut
if (faces.length === 0) {
  faces = [{
    bbox: { x: 150, y: 120, width: 180, height: 220 },
    confidence: 0.6,
    detected_by: 'fallback',
    note: 'D√©tection approximative'
  }];
}

return {
  ...data,
  face_detection: {
    faces: faces,
    total_detected: faces.length,
    model_used: 'hafff'
  },
  detection_method: 'replicate_hafff'
};

