// Encoder image en base64 pour Replicate
const fs = require('fs');
const data = $input.first().json;

try {
  const imageBuffer = fs.readFileSync(data.frame_path);
  const base64Image = imageBuffer.toString('base64');

  return {
    ...data,
    image_base64: base64Image,
    image_data_url: `data:image/png;base64,${base64Image}`
  };
} catch (error) {
  // Frame non trouv√©e, cr√©er une image factice pour continuer
  return {
    ...data,
    skip_frame: true,
    error: `Frame manquante: ${error.message}`
  };
}

// Cr√©er liste des frames pour d√©tection Replicate
const data = $input.first().json;
const frameCount = 8; // Optimis√© pour Replicate
const frames = [];

for (let i = 1; i <= frameCount; i++) {
  frames.push({
    ...data,
    frame_number: i,
    frame_path: `/tmp/${data.workflow_id}/frames/frame_${String(i).padStart(3, '0')}.png`,
    timestamp: (i-1) * 2.5
  });
}

return frames;

// Traiter r√©ponse TikTok
const response = $input.first().json;
const prevData = $input.all()[0].json;

if (response.code !== 0) {
  throw new Error(`Erreur t√©l√©chargement: ${response.msg}`);
}

const video = response.data;

return {
  ...prevData,
  video_url: video.hdplay || video.play,
  video_title: video.title || 'TikTok Video',
  duration: video.duration || 30,
  author: video.author?.nickname || 'Unknown'
};

// Validation URL TikTok et tokens
const data = $input.first().json;

if (!data.url || !data.url.includes('tiktok.com')) {
  throw new Error('URL TikTok invalide ou manquante');
}

const replicateToken = data.replicate_token || 'YOUR_REPLICATE_TOKEN';

if (!replicateToken || replicateToken === 'YOUR_REPLICATE_TOKEN') {
  throw new Error('Token Replicate manquant ou invalide');
}

const workflowId = `wf_${Date.now()}`;

return {
  tiktok_url: data.url,
  replicate_token: replicateToken,
  workflow_id: workflowId,
  timestamp: new Date().toISOString()
};
