  };
} else if (designGeneration.status === 'in_progress') {
  return {
    status: 'in_progress',
    job_id: designGeneration.job_id
  };
} else {
  throw new Error('Erreur dans la g√©n√©ration du design');
}

// Attendre que l'export soit termin√©
