const items = $input.all();

if (items.length < 8) {
  return {
    needNewQuestions: true,
    hasQuestions: false,
    availableCount: items.length
  };
}

// M√©langer et s√©lectionner 8 questions
const shuffled = [...items].sort(() => 0.5 - Math.random());
const selectedQuestions = shuffled.slice(0, 8);

