const extractProperty = (prop) => {
  if (prop?.title && prop.title[0]?.text?.content) return prop.title[0].text.content;
  if (prop?.rich_text && prop.rich_text[0]?.text?.content) return prop.rich_text[0].text.content;
  if (prop?.select?.name) return prop.select.name;
  if (prop?.checkbox !== undefined) return prop.checkbox;
  return '';
};

// Formatter les 8 questions
const formattedQuestions = selectedQuestions.map((item, index) => {
  const props = item.json.properties;
  return {
    id: item.json.id,
    questionNumber: index + 1,
    question: extractProperty(props.Question),
    option_a: extractProperty(props['Option A']),
    option_b: extractProperty(props['Option B']),
    option_c: extractProperty(props['Option C']),
    option_d: extractProperty(props['Option D']),
