// Parser la r√©ponse OpenAI pour extraire 8 questions
const response = $input.first().json.message.content;
const questions = [];

for (let i = 1; i <= 8; i++) {
  try {
    const questionRegex = new RegExp(`QUESTION_${i}:\\s*(.+?)(?=\\n[A-D]\\))`, 's');
    const correctRegex = new RegExp(`CORRECT_${i}:\\s*([A-D])`);
    const explanationRegex = new RegExp(`EXPLICATION_${i}:\\s*(.+?)(?=\\nCATEGORIE_${i}|\\nQUESTION_${i+1}|$)`, 's');
    const categoryRegex = new RegExp(`CAT√âGORIE_${i}:\\s*(.+?)(?=\\nQUESTION_${i+1}|$)`, 's');

    const questionMatch = response.match(questionRegex);
    const correctMatch = response.match(correctRegex);
    const explanationMatch = response.match(explanationRegex);
    const categoryMatch = response.match(categoryRegex);

    if (questionMatch && correctMatch) {
      // Extraire les options pour cette question
      const questionSection = response.substring(
        response.indexOf(`QUESTION_${i}`),
        response.indexOf(`QUESTION_${i+1}`) > -1 ? response.indexOf(`QUESTION_${i+1}`) : response.length
      );

      const options = {};
      const optionMatches = questionSection.match(/([A-D])\)\s*(.+?)(?=\n|$)/g);
      if (optionMatches) {
        optionMatches.forEach(option => {
          const match = option.match(/([A-D])\)\s*(.+)/);
          if (match) {
            options[match[1]] = match[2].trim();
          }
        });
      }

      questions.push({
        questionNumber: i,
        question: questionMatch[1].trim(),
        option_a: options.A || '',
        option_b: options.B || '',
        option_c: options.C || '',
        option_d: options.D || '',
        correct_answer: correctMatch[1],
        explanation: explanationMatch ? explanationMatch[1].trim() : '',
        category: categoryMatch ? categoryMatch[1].trim() : 'Culture g√©n√©rale'
      });
    }
  } catch (error) {
    console.log(`Erreur parsing question ${i}:`, error.message);
  }
}

if (questions.length === 0) {
  throw new Error('Aucune question g√©n√©r√©e correctement');
}

return {
  questions,
  hasQuestions: true,
  needNewQuestions: false,
  isNewQuestions: true
};

