const maxAttempts = 20;
let attempt = 0;

while (attempt < maxAttempts) {
  const designData = $input.first().json;

  if (designData.status === 'success' && designData.generated_designs && designData.generated_designs.length > 0) {
    return {
      status: 'completed',
      designs: designData.generated_designs,
      design_id: designData.generated_designs[0].id
    };
  } else if (designData.status === 'failed') {
    throw new Error('G√©n√©ration design √©chou√©e');
  }

  // Attendre 5 secondes avant de r√©essayer
  await new Promise(resolve => setTimeout(resolve, 5000));
  attempt++;
}

throw new Error('Timeout: Design non g√©n√©r√© apr√®s 100 secondes');

// Attendre que l'export soit termin√©
const maxAttempts = 30;
let attempt = 0;

while (attempt < maxAttempts) {
  const exportData = $input.first().json;

  if (exportData.status === 'success' && exportData.url) {
    return {
      status: 'completed',
      download_url: exportData.url,
      job_id: exportData.job_id
    };
  } else if (exportData.status === 'failed') {
    throw new Error('Export vid√©o √©chou√©');
  }

  // Attendre 10 secondes avant de r√©essayer
  await new Promise(resolve => setTimeout(resolve, 10000));
  attempt++;
}

throw new Error('Timeout: Export non termin√© apr√®s 5 minutes');

// Marquer toutes les questions utilis√©es comme 'used'
const questionsData = $('Prepare Content1').item.json.questions;
const videoUrl = $json.download_url;

// R√©cup√©rer seulement les IDs des questions existantes (pas les nouvelles g√©n√©r√©es)
const questionIds = questionsData
  .filter(q => q.id && typeof q.id === 'string' && !q.id.includes('new_'))
  .map(q => q.id);

return {
  questionIds,
  videoUrl,
  questionsCount: questionsData.length,
  usedCount: questionIds.length
};

// Pr√©parer la notification finale
const questionsData = $('Prepare Content1').item.json.questions;
const videoUrl = $('Wait Export').item.json.download_url;
const categories = $('Prepare Content1').item.json.categories;

const summary = questionsData.slice(0, 3).map((q, i) => 
  `${i + 1}. ${q.question.substring(0, 50)}... (${q.correct_answer})`
).join('\n');

return {
  success: true,
  message: `üé• Vid√©o quiz de ${questionsData.length} questions g√©n√©r√©e !`,
  video_url: videoUrl,
  total_questions: questionsData.length,
  categories: categories.join(', '),
