  preview: summary + (questionsData.length > 3 ? `\n... et ${questionsData.length - 3} autres questions` : ''),
  created_at: new Date().toISOString()
};

// Pr√©parer le contenu pour toutes les questions disponibles
let questionsData;
const inputItems = $input.all();

// Trouver les donn√©es de questions (existantes ou nouvelles)
for (const item of inputItems) {
  if (item.json.hasQuestions && item.json.questions && Array.isArray(item.json.questions)) {
    questionsData = item.json.questions;
    break;
  }
}

if (!questionsData || questionsData.length === 0) {
  throw new Error('Aucune question trouv√©e');
}

// Limiter √† 8 questions max
if (questionsData.length > 8) {
  questionsData = questionsData.slice(0, 8);
}

// Cr√©er le script de voix off simplifi√©
let voiceScript = `Bonjour et bienvenue √† ce quiz de culture g√©n√©rale !\nVous allez voir ${questionsData.length} questions avec leurs options √† l'√©cran.\nVous aurez 5 secondes pour r√©fl√©chir avant que je r√©v√®le la r√©ponse.\nC'est parti !\n\n`;

// Ajouter chaque question
questionsData.forEach((q, index) => {
