// Validation et extraction de l'URL TikTok
const inputData = $input.first().json;
const tiktokUrl = inputData.url || inputData.tiktok_url;

if (!tiktokUrl) {
  throw new Error('URL TikTok manquante');
}

// Vérification format URL TikTok
if (!tiktokUrl.includes('tiktok.com')) {
  throw new Error('URL TikTok invalide');
}

return {
  json: {
    tiktok_url: tiktokUrl,
    timestamp: new Date().toISOString(),
    workflow_id: `workflow_${Date.now()}`
  }
};

// Traitement de la réponse de téléchargement
