// Script Python pour détecter les visages avec MediaPipe
const pythonScript = `
import cv2
import mediapipe as mp
import json
import glob
import os

mp_face_detection = mp.solutions.face_detection
mp_drawing = mp.solutions.drawing_utils

workflow_id = "${$json.workflow_id}"
frames_path = f"/tmp/frames_{workflow_id}/*.png"
results = []

with mp_face_detection.FaceDetection(model_selection=0, min_detection_confidence=0.5) as face_detection:
    frame_files = sorted(glob.glob(frames_path))

    for i, frame_path in enumerate(frame_files):
        image = cv2.imread(frame_path)
        image_rgb = cv2.cvtColor(image, cv2.COLOR_BGR2RGB)

        detection_results = face_detection.process(image_rgb)

        if detection_results.detections:
            for j, detection in enumerate(detection_results.detections):
                bbox = detection.location_data.relative_bounding_box
                results.append({
                    "frame_number": i,
                    "face_id": j,
                    "timestamp": i * 0.5,  # 2 FPS
                    "bbox": {
                        "x": bbox.xmin,
                        "y": bbox.ymin,
                        "width": bbox.width,
                        "height": bbox.height
                    },
                    "confidence": detection.score[0],
                    "frame_path": frame_path
                })

with open(f"/tmp/faces_{workflow_id}.json", "w") as f:
    json.dump(results, f)

print(json.dumps({"faces_detected": len(results), "output_file": f"/tmp/faces_{workflow_id}.json"}))
`;

return {
  json: {
    python_script: pythonScript,
    workflow_id: $json.workflow_id
  }
};

// Lire et traiter les résultats de détection
const fs = require('fs');
const workflowId = $('Préparer Détection Visages').first().json.workflow_id;
const facesFile = `/tmp/faces_${workflowId}.json`;

try {
  const facesData = JSON.parse(fs.readFileSync(facesFile, 'utf8'));

  // Grouper les visages par position pour identifier les différentes personnes
  const speakers = [];
  const threshold = 0.3; // Seuil de similarité de position

  facesData.forEach(face => {
    let assigned = false;

    for (let speaker of speakers) {
      const avgX = speaker.faces.reduce((sum, f) => sum + f.bbox.x, 0) / speaker.faces.length;
      const avgY = speaker.faces.reduce((sum, f) => sum + f.bbox.y, 0) / speaker.faces.length;

      if (Math.abs(face.bbox.x - avgX) < threshold && Math.abs(face.bbox.y - avgY) < threshold) {
        speaker.faces.push(face);
        assigned = true;
        break;
      }
    }

    if (!assigned) {
      speakers.push({
        speaker_id: speakers.length,
        faces: [face],
        avg_position: { x: face.bbox.x, y: face.bbox.y }
      });
    }
  });

  return speakers.map(speaker => ({
    json: {
      speaker_id: speaker.speaker_id,
      face_count: speaker.faces.length,
      first_appearance: speaker.faces[0].timestamp,
      last_appearance: speaker.faces[speaker.faces.length - 1].timestamp,
      avg_position: speaker.avg_position,
      sample_frame: speaker.faces[0].frame_path,
      workflow_id: workflowId
    }
  }));

} catch (error) {
  throw new Error(`Erreur lecture détection: ${error.message}`);
}

// Préparer la transformation en avatar enfant
const speakerData = $input.first().json;

// Extraire le visage de l'image de référence
const extractCommand = `ffmpeg -i "${speakerData.sample_frame}" -vf "crop=w*${speakerData.avg_position.x + 0.2}:h*${speakerData.avg_position.y + 0.2}:w*${speakerData.avg_position.x}:h*${speakerData.avg_position.y}" /tmp/face_${speakerData.workflow_id}_${speakerData.speaker_id}.png`;

return {
  json: {
    extract_command: extractCommand,
    face_file: `/tmp/face_${speakerData.workflow_id}_${speakerData.speaker_id}.png`,
    speaker_id: speakerData.speaker_id,
    workflow_id: speakerData.workflow_id
  }
};

// Préparer l'animation avec Wav2Lip
const transformResult = $input.first().json;
const speakerData = $('Préparer Extraction Visage').first().json;

// Commande Wav2Lip pour animer le visage
const wav2lipCommand = `python wav2lip/inference.py --checkpoint_path checkpoints/wav2lip.pth --face "${transformResult.output[0]}" --audio "/tmp/audio_${speakerData.workflow_id}.wav" --outfile "/tmp/animated_${speakerData.workflow_id}_${speakerData.speaker_id}.mp4"`;

return {
  json: {
    wav2lip_command: wav2lipCommand,
    animated_file: `/tmp/animated_${speakerData.workflow_id}_${speakerData.speaker_id}.mp4`,
    child_avatar: transformResult.output[0],
    speaker_id: speakerData.speaker_id,
    workflow_id: speakerData.workflow_id
  }
};

// Assembler la vidéo finale
const animationData = $input.all();
const workflowId = animationData[0].json.workflow_id;

// Créer la commande FFmpeg pour assembler toutes les animations
let overlayFilter = '[0:v]';
let inputs = `-i /tmp/video_${workflowId}.mp4`;

animationData.forEach((data, index) => {
  inputs += ` -i "${data.json.animated_file}"`;
  const nextIndex = index + 1;
  const outputLabel = index === animationData.length - 1 ? '[out]' : `[v${nextIndex}]`;
  overlayFilter += `overlay=0:0:enable='between(t,${data.json.start_time || 0},${data.json.end_time || 999})'${outputLabel}`;
  if (index < animationData.length - 1) {
    overlayFilter = `${outputLabel}`;
  }
});

const finalCommand = `ffmpeg ${inputs} -filter_complex "${overlayFilter}" -map "[out]" -map 0:a -c:a copy /tmp/final_${workflowId}.mp4`;

return {
  json: {
    final_command: finalCommand,
    final_video: `/tmp/final_${workflowId}.mp4`,
    workflow_id: workflowId,
    speakers_count: animationData.length
  }
};

// Réponse finale avec les résultats
const finalData = $input.first().json;

return {
  json: {
    success: true,
    message: "Transformation terminée avec succès",
    final_video_path: finalData.final_video,
    workflow_id: finalData.workflow_id,
    speakers_processed: finalData.speakers_count,
    download_url: `http://your-server.com/download/${finalData.workflow_id}`,
    processing_time: new Date().toISOString()
  }
};
